'use client';

import { DEFAULT_PAGE_SIZE_OPTIONS } from '@/constants/common';
import { cn } from '@/lib/utils';
import { SelectGroup } from '@radix-ui/react-select';
import {
    flexRender,
    getCoreRowModel,
    useReactTable,
    type Cell,
    type ColumnDef,
    type FilterFn,
    type HeaderGroup,
    type PaginationOptions,
    type PaginationState,
    type Row,
    type SortingState,
} from '@tanstack/react-table';
import { ArrowDown, ArrowUp, ArrowUpDown } from 'lucide-react';
import * as React from 'react';
import { Button } from './button';
import { Input } from './input';
import { Pagination, PaginationContent, PaginationItem, PaginationRange } from './pagination';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './select';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from './table';

interface DataTableProps<T extends Record<string, unknown>> {
  data: T[];
  columns: ColumnDef<T, unknown>[];
  pagination: PaginationState;
  paginationOptions: Pick<PaginationOptions, 'onPaginationChange' | 'rowCount'>;
  totalPages?: number;
  searchable?: boolean;
  searchPlaceholder?: string;
  searchKey?: keyof T | string;
  emptyMessage?: string;
  className?: string;
  enableGlobalFilter?: boolean;
  globalFilterFn?: FilterFn<T>;
  onRowClick?: (row: T) => void;
  rowClassName?: (row: T) => string;
}

function DataTable<T extends Record<string, unknown>>({
  data,
  paginationOptions,
  columns,
  totalPages = 0,
  searchable = false,
  searchPlaceholder,
  searchKey,
  emptyMessage,
  className,
  enableGlobalFilter = true,
  globalFilterFn,
  pagination,
  onRowClick,
  rowClassName,
}: DataTableProps<T>) {
  const [sorting, setSorting] = React.useState<SortingState>([]);
  const [globalFilter, setGlobalFilter] = React.useState('');

  const table = useReactTable({
    data,
    columns,
    getRowId: (row, index) => {
      // Try to use 'id' field from row, fallback to index if not available
      if ('id' in row && row.id != null) {
        return String(row.id);
      }
      // Fallback to index as string
      return String(index);
    },
    getCoreRowModel: getCoreRowModel(),
    onSortingChange: setSorting,
    manualFiltering: true,
    manualSorting: true,
    manualPagination: true,
    globalFilterFn:
      globalFilterFn ||
      ((row, _columnId, filterValue) => {
        if (!searchKey) {
          // Search all columns
          return Object.values(row.original).some((value) =>
            String(value).toLowerCase().includes(String(filterValue).toLowerCase()),
          );
        }
        // Search specific column
        const value = row.original[searchKey as keyof T];
        return String(value).toLowerCase().includes(String(filterValue).toLowerCase());
      }),
    enableGlobalFilter: searchable && enableGlobalFilter,
    state: {
      sorting,
      pagination,
      columnPinning: {
        right: ['actions'],
      },
    },
    ...paginationOptions,
  });

  return (
    <div className={cn('space-y-4', className)}>
      {searchable && (
        <Input
          placeholder={searchPlaceholder || 'Search...'}
          value={globalFilter ?? ''}
          onChange={(e) => setGlobalFilter(e.target.value)}
          className="max-w-sm"
        />
      )}

      <div className="rounded-md border">
        <Table>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup: HeaderGroup<T>) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => {
                  const canSort = header.column.getCanSort();
                  const isSorted = header.column.getIsSorted();

                  return (
                    <TableHead key={header.id}>
                      {header.isPlaceholder ? null : (
                        <div className="flex gap-2 items-center">
                          <div
                            className={cn(
                              canSort && 'cursor-pointer select-none hover:text-foreground',
                            )}
                            onClick={canSort ? header.column.getToggleSortingHandler() : undefined}
                          >
                            {flexRender(header.column.columnDef.header, header.getContext())}
                          </div>
                          {canSort && (
                            <Button
                              variant="ghost"
                              size="icon"
                              className="w-4 h-4"
                              onClick={header.column.getToggleSortingHandler()}
                            >
                              {isSorted === 'asc' ? (
                                <ArrowUp className="size-3.5 text-primary" />
                              ) : isSorted === 'desc' ? (
                                <ArrowDown className="size-3.5 text-primary" />
                              ) : (
                                <ArrowUpDown className="size-3.5" />
                              )}
                            </Button>
                          )}
                        </div>
                      )}
                    </TableHead>
                  );
                })}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              table.getRowModel().rows.map((row: Row<T>) => (
                <TableRow
                  key={row.id}
                  data-state={row.getIsSelected() && 'selected'}
                  className={cn(
                    onRowClick && 'cursor-pointer',
                    rowClassName && rowClassName(row.original),
                  )}
                  onClick={() => onRowClick?.(row.original)}
                >
                  {row.getVisibleCells().map((cell: Cell<T, unknown>) => (
                    <TableCell key={cell.id}>
                      {flexRender(cell.column.columnDef.cell, cell.getContext())}
                    </TableCell>
                  ))}
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell colSpan={table.getAllColumns().length} className="py-8 text-center">
                  {emptyMessage || 'No data available'}
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      {typeof totalPages === 'number' && totalPages > 1 && (
        <DataTablePagination table={table} totalPages={totalPages} />
      )}
    </div>
  );
}

interface DataTablePaginationProps<TData> {
  table: ReturnType<typeof useReactTable<TData>>;
  totalPages: number;
}

function DataTablePagination<TData>({ table, totalPages }: DataTablePaginationProps<TData>) {
  return (
    <Pagination>
      <PaginationContent className="flex-1 gap-4 justify-end">
        <PaginationItem>
          <PaginationRange
            currentPage={table.getState().pagination.pageIndex + 1}
            pageSize={table.getState().pagination.pageSize}
            totalPages={totalPages}
            isGroupButton
            onPageChange={(page) => {
              table.setPageIndex(page - 1);
            }}
          />
        </PaginationItem>
        <PaginationItem>
          <Select
            onValueChange={(value) => {
              table.setPageSize(Number(value));
            }}
            value={table.getState().pagination.pageSize.toString()}
          >
            <SelectTrigger className="w-[130px]" size={'sm'}>
              <SelectValue placeholder="Chn" />
            </SelectTrigger>
            <SelectContent>
              <SelectGroup>
                {DEFAULT_PAGE_SIZE_OPTIONS.map((option) => (
                  <SelectItem key={option.id} value={option.id.toString()}>
                    {option.name}
                  </SelectItem>
                ))}
              </SelectGroup>
            </SelectContent>
          </Select>
        </PaginationItem>
      </PaginationContent>
    </Pagination>
  );
}

export type { ColumnDef } from '@tanstack/react-table';
export { DataTable };

'use client';

import { format, setHours, setMinutes, setSeconds, startOfMonth, type Locale } from 'date-fns';
import { CalendarIcon } from 'lucide-react';
import * as React from 'react';
import type { DayPickerSingleProps } from 'react-day-picker';

import { Button } from '@/components/ui/button';
import { Calendar } from '@/components/ui/calendar';
import { Label } from '@/components/ui/label';
import { MonthPicker } from '@/components/ui/month-picker';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Separator } from '@/components/ui/separator';
import { TimePicker, type TimeValue } from '@/components/ui/time-picker';
import { type FormSize } from '@/constants/form-sizes';
import { cn } from '@/lib/utils';
import type { ComponentProps } from 'react';

export type DatePickerProps = Omit<
  DayPickerSingleProps,
  'selected' | 'onSelect' | 'mode' | 'required'
> & {
  value?: Date;
  onChange?: (date: Date | undefined) => void;
  placeholder?: string;
  dateFormat?: string;
  showTime?: boolean;
  timeOnly?: boolean;
  mode?: 'single' | 'month';
  label?: string | React.ReactNode;
  error?: string;
  required?: boolean;
  disabled?: boolean;
  disabledPast?: boolean;
  disabledFuture?: boolean;
  onDisabled?: (date: Date) => boolean;
  size?: FormSize;
  className?: ComponentProps<'div'>['className'];
  triggerClassName?: ComponentProps<typeof Button>['className'];
  popoverClassName?: ComponentProps<typeof PopoverContent>['className'];
  locale?: string | Locale;
  cancelText?: string;
  applyText?: string;
  monthNames?: string[];
};

function DatePicker({
  value,
  onChange,
  placeholder = 'Pick a date',
  dateFormat = 'dd/MM/yyyy',
  showTime = false,
  timeOnly = false,
  mode = 'single',
  label,
  error,
  required,
  disabled = false,
  disabledPast = false,
  disabledFuture = false,
  onDisabled,
  size = 'md',
  className,
  triggerClassName,
  popoverClassName,
  locale,
  cancelText,
  applyText,
  monthNames,
  ...calendarProps
}: DatePickerProps) {
  const [open, setOpen] = React.useState(false);
  const [tempDate, setTempDate] = React.useState<Date | undefined>(value);
  const [currentMonth, setCurrentMonth] = React.useState<Date>(() => value || new Date());
  const [selectedTime, setSelectedTime] = React.useState<TimeValue>(() => {
    if (value) {
      return {
        hour: value.getHours().toString().padStart(2, '0'),
        minute: value.getMinutes().toString().padStart(2, '0'),
        second: value.getSeconds().toString().padStart(2, '0'),
      };
    }
    return { hour: '00', minute: '00', second: '00' };
  });


  // Store initial values when opening popover
  const initialDateRef = React.useRef<Date | undefined>(value);
  const initialMonthRef = React.useRef<Date>(value || new Date());
  const getInitialTime = (): TimeValue => {
    if (value) {
      return {
        hour: value.getHours().toString().padStart(2, '0'),
        minute: value.getMinutes().toString().padStart(2, '0'),
        second: value.getSeconds().toString().padStart(2, '0'),
      };
    }
    return { hour: '00', minute: '00', second: '00' };
  };
  const initialTimeRef = React.useRef<TimeValue>(getInitialTime());

  React.useEffect(() => {
    if (open) {
      // Store initial values when opening
      initialDateRef.current = value;
      initialMonthRef.current = value || new Date();
      initialTimeRef.current = value
        ? {
            hour: value.getHours().toString().padStart(2, '0'),
            minute: value.getMinutes().toString().padStart(2, '0'),
            second: value.getSeconds().toString().padStart(2, '0'),
          }
        : { hour: '00', minute: '00', second: '00' };

      // Update currentMonth and tempDate to match value when opening
      if (timeOnly) {
        // For time only, don't set tempDate or currentMonth
        if (value) {
          setSelectedTime({
            hour: value.getHours().toString().padStart(2, '0'),
            minute: value.getMinutes().toString().padStart(2, '0'),
            second: value.getSeconds().toString().padStart(2, '0'),
          });
        } else {
          setSelectedTime({ hour: '00', minute: '00', second: '00' });
        }
      } else if (mode === 'month') {
        // For month picker, set tempDate
        if (value) {
          setTempDate(startOfMonth(value));
        } else {
          setTempDate(startOfMonth(new Date()));
        }
      } else {
        setTempDate(value);
        if (value) {
          setCurrentMonth(value);
          if (showTime) {
            setSelectedTime({
              hour: value.getHours().toString().padStart(2, '0'),
              minute: value.getMinutes().toString().padStart(2, '0'),
              second: value.getSeconds().toString().padStart(2, '0'),
            });
          }
        } else {
          setCurrentMonth(new Date());
          if (showTime) {
            setSelectedTime({ hour: '00', minute: '00', second: '00' });
          }
        }
      }
    }
  }, [open, value, showTime, timeOnly, mode]);

  // Only sync when popover is closed to avoid conflicts with open effect
  React.useEffect(() => {
    if (!open && !timeOnly) {
      setTempDate(value);
      if (value) {
        setCurrentMonth(value);
        if (showTime) {
          setSelectedTime({
            hour: value.getHours().toString().padStart(2, '0'),
            minute: value.getMinutes().toString().padStart(2, '0'),
            second: value.getSeconds().toString().padStart(2, '0'),
          });
        }
      }
    } else if (!open && timeOnly && value) {
      // For time only, only update time when closed
      setSelectedTime({
        hour: value.getHours().toString().padStart(2, '0'),
        minute: value.getMinutes().toString().padStart(2, '0'),
        second: value.getSeconds().toString().padStart(2, '0'),
      });
    }
  }, [value, showTime, timeOnly, open]);

  const handleApply = () => {
    if (timeOnly) {
      // For time only, use current date or today's date with selected time
      const baseDate = value || new Date();
      const finalDate = setSeconds(
        setMinutes(setHours(baseDate, parseInt(selectedTime.hour, 10)), parseInt(selectedTime.minute, 10)),
        parseInt(selectedTime.second, 10),
      );
      onChange?.(finalDate);
    } else if (mode === 'month') {
      // For month picker, use start of month
      if (tempDate) {
        onChange?.(startOfMonth(tempDate));
      } else {
        onChange?.(undefined);
      }
    } else if (tempDate) {
      let finalDate = tempDate;
      if (showTime) {
        finalDate = setHours(finalDate, parseInt(selectedTime.hour, 10));
        finalDate = setMinutes(finalDate, parseInt(selectedTime.minute, 10));
        finalDate = setSeconds(finalDate, parseInt(selectedTime.second, 10));
      }
      onChange?.(finalDate);
    } else {
      onChange?.(undefined);
    }
    setOpen(false);
  };

  const handleCancel = () => {
    // Reset to initial values when popover was opened
    setTempDate(initialDateRef.current);
    setCurrentMonth(initialMonthRef.current);
    if (showTime) {
      setSelectedTime(initialTimeRef.current);
    }
    setOpen(false);
  };

  const displayValue = React.useMemo(() => {
    if (!value) return placeholder;
    // Only use locale if it's a Locale object (not string)
    const formatOptions = locale && typeof locale !== 'string' ? { locale } : undefined;
    if (timeOnly) {
      // Time only format
      const hasTimeFormat = /[Hhms]/.test(dateFormat);
      if (hasTimeFormat) {
        return format(value, dateFormat, formatOptions);
      }
      return format(value, 'HH:mm:ss', formatOptions);
    }
    if (mode === 'month') {
      // Month picker format - use locale-aware format
      if (locale) {
        const localeString = typeof locale === 'string' ? locale : locale?.code || 'en-US';
        const formatter = new Intl.DateTimeFormat(localeString, { month: '2-digit', year: 'numeric' });
        return formatter.format(value);
      }
      return format(value, 'MM/yyyy', formatOptions);
    }
    if (showTime) {
      // Check if dateFormat already includes time format
      const hasTimeFormat = /[Hhms]/.test(dateFormat);
      if (hasTimeFormat) {
        return format(value, dateFormat, formatOptions);
      }
      return format(value, `${dateFormat} HH:mm:ss`, formatOptions);
    }
    return format(value, dateFormat, formatOptions);
  }, [value, dateFormat, showTime, timeOnly, mode, placeholder, locale]);


  const handleCheckDisabled = React.useCallback(
    (date: Date) => {
      if (disabled) return true;

      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const compareDate = new Date(date);
      compareDate.setHours(0, 0, 0, 0);

      if (disabledPast && compareDate < today) {
        return true;
      }

      if (disabledFuture && compareDate > today) {
        return true;
      }

      return onDisabled ? onDisabled(date) : false;
    },
    [disabled, disabledPast, disabledFuture, onDisabled],
  );


  const calendarPropsWithMode = React.useMemo(
    () =>
      ({
        ...calendarProps,
        mode: 'single' as const,
        selected: tempDate,
        onSelect: setTempDate,
        defaultMonth: currentMonth,
        month: currentMonth,
        onMonthChange: setCurrentMonth,
        captionLayout: 'dropdown' as const,
        disabled: handleCheckDisabled,
        startMonth: new Date(1890, 0, 1),
        endMonth: new Date(new Date().getFullYear() + 50, 11, 31),
        locale: locale,
      }) as React.ComponentProps<typeof Calendar>,
    [calendarProps, tempDate, currentMonth, handleCheckDisabled, locale],
  );

  const triggerElement = (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          size={size}
          disabled={disabled}
          className={cn(
            'group w-full justify-between',
            !value && 'text-muted-foreground',
            error && 'border-destructive',
            triggerClassName,
          )}
        >
          <span>{displayValue}</span>
          <CalendarIcon className="h-4 w-4 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent
        className={cn('flex w-auto flex-col gap-2 p-0', popoverClassName)}
        align="start"
      >
        <div className="flex flex-col gap-2 pb-2">
          {timeOnly ? (
            <div className="flex h-[350px]">
              <TimePicker
                value={selectedTime}
                onChange={setSelectedTime}
                className="min-h-0 h-full shrink-0 w-full"
              />
            </div>
          ) : mode === 'month' ? (
            <div className="flex h-[350px]">
              <MonthPicker
                value={tempDate}
                onChange={setTempDate}
                locale={locale}
                monthNames={monthNames}
                disabled={handleCheckDisabled}
                className="min-h-0 h-full shrink-0 w-full"
              />
            </div>
          ) : (
            <div className={cn('flex h-[350px]', showTime && 'overflow-hidden')}>
              <Calendar initialFocus {...calendarPropsWithMode} />
              {showTime && (
                <TimePicker
                  value={selectedTime}
                  onChange={setSelectedTime}
                  className="min-h-0 h-full shrink-0"
                />
              )}
            </div>
          )}
          <Separator className="shrink-0" />
          <div className="flex justify-end gap-2 px-2 shrink-0">
            <Button variant="secondary" size="sm" onClick={handleCancel}>
              {cancelText || 'Cancel'}
            </Button>
            <Button size="sm" onClick={handleApply}>
              {applyText || 'Apply'}
            </Button>
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );

  if (label || error) {
    return (
      <div className={cn('w-full', className)}>
        {label && (
          <Label className="text-foreground leading-5 block">
            {label}
            {required && <span className="text-destructive">*</span>}
          </Label>
        )}
        <div className={label ? 'mt-1' : ''}>{triggerElement}</div>
        {error && (
          <div className="text-destructive text-sm mt-1.5" role="alert">
            {error}
          </div>
        )}
      </div>
    );
  }

  return <div className={cn('w-full', className)}>{triggerElement}</div>;
}

export { DatePicker };

'use client';

import {
  endOfDay,
  endOfMonth,
  endOfWeek,
  format,
  startOfDay,
  startOfMonth,
  startOfWeek,
  subMonths,
  subWeeks,
  type Locale,
} from 'date-fns';
import { CalendarIcon } from 'lucide-react';
import * as React from 'react';
import type { DateRange, DayPickerRangeProps } from 'react-day-picker';

import { Button } from '@/components/ui/button';
import { Calendar } from '@/components/ui/calendar';
import { Label } from '@/components/ui/label';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Separator } from '@/components/ui/separator';
import { type FormSize } from '@/constants/form-sizes';
import { cn } from '@/lib/utils';
import type { ComponentProps } from 'react';

export type DateRangePreset = {
  label: string;
  range: DateRange;
};

export type DateRangePickerProps = Omit<
  DayPickerRangeProps,
  'selected' | 'onSelect' | 'mode' | 'required'
> & {
  value?: DateRange;
  onChange?: (range: DateRange | undefined) => void;
  placeholder?: { from?: string; to?: string };
  dateFormat?: string;
  presets?: DateRangePreset[];
  showPresets?: boolean;
  label?: string | React.ReactNode;
  error?: string;
  required?: boolean;
  disabled?: boolean;
  disabledPast?: boolean;
  disabledFuture?: boolean;
  onDisabled?: (date: Date) => boolean;
  size?: FormSize;
  className?: ComponentProps<'div'>['className'];
  triggerClassName?: ComponentProps<typeof Button>['className'];
  popoverClassName?: ComponentProps<typeof PopoverContent>['className'];
  locale?: string | Locale;
  cancelText?: string;
  applyText?: string;
};

const getDefaultPresets = (): DateRangePreset[] => {
  const today = new Date();
  return [
    {
      label: 'Hm nay',
      range: {
        from: startOfDay(today),
        to: endOfDay(today),
      },
    },
    {
      label: 'Tun trc',
      range: {
        from: startOfWeek(subWeeks(today, 1), { weekStartsOn: 1 }),
        to: endOfWeek(subWeeks(today, 1), { weekStartsOn: 1 }),
      },
    },
    {
      label: 'Thng trc',
      range: {
        from: startOfMonth(subMonths(today, 1)),
        to: endOfMonth(subMonths(today, 1)),
      },
    },
    {
      label: '3 thng trc',
      range: {
        from: startOfMonth(subMonths(today, 3)),
        to: endOfMonth(subMonths(today, 3)),
      },
    },
  ];
};

function DateRangePicker({
  value,
  onChange,
  placeholder = { from: 'From date', to: 'To date' },
  dateFormat = 'dd/MM/yyyy',
  presets,
  showPresets,
  label,
  error,
  required,
  disabled = false,
  disabledPast = false,
  disabledFuture = false,
  onDisabled,
  size = 'md',
  className,
  triggerClassName,
  popoverClassName,
  locale,
  cancelText,
  applyText,
  ...calendarProps
}: DateRangePickerProps) {
  const [open, setOpen] = React.useState(false);
  const [tempRange, setTempRange] = React.useState<DateRange | undefined>(value);
  const [currentMonth, setCurrentMonth] = React.useState<Date>(() => {
    // Use end date (to) if available, otherwise use start date (from)
    return value?.to || value?.from || new Date();
  });

  // Store initial values when opening popover
  const initialRangeRef = React.useRef<DateRange | undefined>(value);
  const initialMonthRef = React.useRef<Date>(value?.to || value?.from || new Date());

  React.useEffect(() => {
    if (open) {
      // Store initial values when opening
      initialRangeRef.current = value;
      initialMonthRef.current = value?.to || value?.from || new Date();

      // Update tempRange and currentMonth to match value when opening
      setTempRange(value);
      if (value?.to) {
        // Show month of end date if available
        setCurrentMonth(value.to);
      } else if (value?.from) {
        // Show month of start date if no end date
        setCurrentMonth(value.from);
      } else {
        setCurrentMonth(new Date());
      }
    }
  }, [open, value]);

  React.useEffect(() => {
    setTempRange(value);
    if (value?.to) {
      setCurrentMonth(value.to);
    } else if (value?.from) {
      setCurrentMonth(value.from);
    }
  }, [value]);

  const handleCheckDisabled = React.useCallback(
    (date: Date) => {
      if (disabled) return true;

      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const compareDate = new Date(date);
      compareDate.setHours(0, 0, 0, 0);

      if (disabledPast && compareDate < today) {
        return true;
      }

      if (disabledFuture && compareDate > today) {
        return true;
      }

      return onDisabled ? onDisabled(date) : false;
    },
    [disabled, disabledPast, disabledFuture, onDisabled],
  );

  const defaultPresets = React.useMemo(() => getDefaultPresets(), []);
  const displayPresets = presets ?? defaultPresets;
  const shouldShowPresets = showPresets !== undefined ? showPresets : displayPresets.length > 0;

  const handleApply = () => {
    onChange?.(tempRange);
    setOpen(false);
  };

  const handleCancel = () => {
    // Reset to initial values when popover was opened
    setTempRange(initialRangeRef.current);
    setCurrentMonth(initialMonthRef.current);
    setOpen(false);
  };

  const handlePresetClick = (preset: DateRangePreset) => {
    setTempRange(preset.range);
    // Update currentMonth to navigate calendar to the preset range
    // Prefer end date (to) if available, otherwise use start date (from)
    if (preset.range.to) {
      setCurrentMonth(preset.range.to);
    } else if (preset.range.from) {
      setCurrentMonth(preset.range.from);
    }
  };

  const displayValue = React.useMemo(() => {
    if (!value?.from) return placeholder.from || 'Pick a date';
    // Only use locale if it's a Locale object (not string)
    const formatOptions = locale && typeof locale !== 'string' ? { locale } : undefined;
    if (value.from && value.to) {
      return `${format(value.from, dateFormat, formatOptions)} - ${format(value.to, dateFormat, formatOptions)}`;
    }
    return format(value.from, dateFormat, formatOptions);
  }, [value, dateFormat, placeholder, locale]);

  const calendarPropsWithMode = React.useMemo(
    () =>
      ({
        ...calendarProps,
        mode: 'range' as const,
        selected: tempRange,
        onSelect: setTempRange,
        defaultMonth: currentMonth,
        month: currentMonth,
        onMonthChange: setCurrentMonth,
        disabled: handleCheckDisabled,
        captionLayout: 'dropdown' as const,
        startMonth: new Date(1890, 0, 1),
        endMonth: new Date(new Date().getFullYear() + 50, 11, 31),
        locale: locale,
      }) as React.ComponentProps<typeof Calendar>,
    [calendarProps, tempRange, currentMonth, handleCheckDisabled, locale],
  );

  const triggerElement = (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          size={size}
          disabled={disabled}
          className={cn(
            'group w-full justify-between',
            !value?.from && 'text-muted-foreground',
            error && 'border-destructive',
            triggerClassName,
          )}
        >
          <span>{displayValue}</span>
          <CalendarIcon className="h-4 w-4 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent
        className={cn('flex w-auto flex-col gap-2 p-0 pb-2', popoverClassName)}
        align="start"
      >
        <div className="flex flex-col gap-2 pb-2">
          <div className="flex">
            {shouldShowPresets && (
              <div className="border-r p-2 w-fit">
                <div className="space-y-1">
                  {displayPresets.map((preset, index) => (
                    <Button
                      key={index}
                      variant="ghost"
                      size="sm"
                      className="block text-left font-normal"
                      onClick={() => handlePresetClick(preset)}
                    >
                      {preset.label}
                    </Button>
                  ))}
                </div>
              </div>
            )}
            <div className="flex-1">
              <Calendar initialFocus {...calendarPropsWithMode} />
            </div>
          </div>
          <Separator />
          <div className="flex justify-end gap-2 px-2">
            <Button variant="secondary" onClick={handleCancel}>
              {cancelText || 'Cancel'}
            </Button>
            <Button onClick={handleApply}>{applyText || 'Apply'}</Button>
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );

  if (label || error) {
    return (
      <div className={cn('w-full', className)}>
        {label && (
          <Label className="text-foreground leading-5 block">
            {label}
            {required && <span className="text-destructive">*</span>}
          </Label>
        )}
        <div className={label ? 'mt-1' : ''}>{triggerElement}</div>
        {error && (
          <div className="text-destructive text-sm mt-1.5" role="alert">
            {error}
          </div>
        )}
      </div>
    );
  }

  return <div className={cn('w-full', className)}>{triggerElement}</div>;
}

export { DateRangePicker };

import { ConfirmDialog } from './confirm-dialog';

interface DeleteConfirmDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  title?: string;
  description?: string;
  itemName?: string;
