  value?: Date;
  onChange: (date: Date | undefined) => void;
  locale?: string | Locale;
  monthNames?: string[];
  disabled?: (date: Date) => boolean;
  className?: string;
};

function MonthPicker({
  value,
  onChange,
  locale,
  monthNames,
  disabled,
  className,
}: MonthPickerProps) {
  const [selectedYear, setSelectedYear] = React.useState<number>(() => {
    return value ? value.getFullYear() : new Date().getFullYear();
  });
  const [selectedMonth, setSelectedMonth] = React.useState<number>(() => {
    return value ? value.getMonth() : new Date().getMonth();
  });
  const monthContainerRef = React.useRef<HTMLDivElement>(null);
  const yearContainerRef = React.useRef<HTMLDivElement>(null);

  // Sync with value prop when it changes (only if different from current state)
  React.useEffect(() => {
    if (value) {
      const year = value.getFullYear();
      const month = value.getMonth();
      // Only update if different to avoid unnecessary re-renders
      if (selectedYear !== year || selectedMonth !== month) {
        setSelectedYear(year);
        setSelectedMonth(month);
      }
    } else {
      // Reset to current month/year if value is undefined
      const now = new Date();
      const nowYear = now.getFullYear();
      const nowMonth = now.getMonth();
      if (selectedYear !== nowYear || selectedMonth !== nowMonth) {
        setSelectedYear(nowYear);
        setSelectedMonth(nowMonth);
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [value]);

  // Scroll to selected month on mount and when selectedMonth changes
  React.useEffect(() => {
    if (!monthContainerRef.current) return;
    
    const timer = setTimeout(() => {
      const monthElement = monthContainerRef.current?.querySelector(
        `[data-month="${selectedMonth}"]`,
      ) as HTMLElement;
      if (monthElement && monthContainerRef.current) {
        const viewport = monthContainerRef.current.closest(
          '[data-slot="scroll-area-viewport"]',
        ) as HTMLElement;
        if (viewport) {
          const elementTop = monthElement.offsetTop;
          viewport.scrollTo({ top: elementTop, behavior: 'smooth' });
        }
      }
    }, 100);
    return () => clearTimeout(timer);
  }, [selectedMonth]);

  // Scroll to selected year on mount and when selectedYear changes
  React.useEffect(() => {
    if (!yearContainerRef.current) return;
    
    const timer = setTimeout(() => {
      const yearElement = yearContainerRef.current?.querySelector(
        `[data-year="${selectedYear}"]`,
      ) as HTMLElement;
      if (yearElement && yearContainerRef.current) {
        const viewport = yearContainerRef.current.closest(
          '[data-slot="scroll-area-viewport"]',
        ) as HTMLElement;
        if (viewport) {
          const elementTop = yearElement.offsetTop;
          viewport.scrollTo({ top: elementTop, behavior: 'smooth' });
        }
      }
    }, 100);
    return () => clearTimeout(timer);
  }, [selectedYear]);

  const handleMonthSelect = React.useCallback(
    (monthIndex: number) => {
      setSelectedMonth(monthIndex);
      const newDate = new Date(selectedYear, monthIndex, 1);
      onChange(startOfMonth(newDate));
    },
    [selectedYear, onChange],
  );

  const handleYearSelect = React.useCallback(
    (year: number) => {
      setSelectedYear(year);
      const newDate = new Date(year, selectedMonth, 1);
      onChange(startOfMonth(newDate));
    },
    [selectedMonth, onChange],
  );

  // Generate month names based on locale or provided monthNames
  const months = React.useMemo(() => {
    if (monthNames && monthNames.length === 12) {
      return monthNames;
    }
    // Use Intl API to get month names based on locale
    const localeString = typeof locale === 'string' ? locale : locale?.code || 'en-US';
    const formatter = new Intl.DateTimeFormat(localeString, { month: 'long' });
    return Array.from({ length: 12 }, (_, i) => {
      const date = new Date(2024, i, 1);
      return formatter.format(date);
    });
  }, [locale, monthNames]);

  const years = React.useMemo(
    () => Array.from({ length: 100 }, (_, i) => new Date().getFullYear() - 50 + i),
    [],
  );

  const displayValue = React.useMemo(() => {
    if (value) {
      return `${months[selectedMonth]} ${selectedYear}`;
    }
    return `${months[new Date().getMonth()]} ${new Date().getFullYear()}`;
  }, [value, months, selectedMonth, selectedYear]);

  return (
    <div className={cn('flex flex-col h-full', className)}>
      {/* Display selected month/year */}
      <div className="px-4 py-3 text-center shrink-0">
        <div className="font-medium text-lg">{displayValue}</div>
      </div>
      {/* Scrollable columns */}
      <div className="flex flex-1 min-h-0 gap-0.5">
        {/* Months column - first column */}
        <ScrollArea className="w-30 [&>[data-slot=scroll-area-viewport]]:rounded-l-md overflow-hidden">
          <div ref={monthContainerRef} className="px-2">
            {months.map((month, index) => {
              const isSelected = selectedMonth === index;
              const isDisabled = disabled ? disabled(new Date(selectedYear, index, 1)) : false;
              return (
                <div
                  key={index}
                  data-month={index}
                  onClick={() => !isDisabled && handleMonthSelect(index)}
                  className={cn(
                    'px-2 py-1.5 text-center cursor-pointer text-sm transition-colors rounded-md',
                    isSelected ? 'bg-secondary' : 'hover:bg-accent',
                    isDisabled && 'opacity-50 cursor-not-allowed',
                  )}
                >
                  {month}
                </div>
              );
            })}
          </div>
        </ScrollArea>
        {/* Years column - second column */}
        <ScrollArea className="flex-1 [&>[data-slot=scroll-area-viewport]]:rounded-r-md overflow-hidden">
          <div ref={yearContainerRef} className="px-2">
            {years.map((year) => {
              const isSelected = selectedYear === year;
              return (
                <div
                  key={year}
                  data-year={year}
                  onClick={() => handleYearSelect(year)}
                  className={cn(
                    'px-2 py-1.5 text-center cursor-pointer text-sm transition-colors rounded-md',
                    isSelected ? 'bg-secondary' : 'hover:bg-accent',
                  )}
                >
                  {year}
                </div>
              );
            })}
          </div>
        </ScrollArea>
      </div>
    </div>
  );
}

export { MonthPicker };

'use client';

import { CheckIcon, ChevronDown, X, XCircle } from 'lucide-react';
import * as React from 'react';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Checkbox } from '@/components/ui/checkbox';
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from '@/components/ui/command';
import { Label } from '@/components/ui/label';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { type FormSize } from '@/constants/form-sizes';
import { cn } from '@/lib/utils';
import type { ComponentProps } from 'react';

type MultipleComboboxBaseOption = {
  id: string;
  code?: string | number;
  name: string;
  disabled?: boolean;
};

type MultipleComboboxValue = string[];

type MultipleComboboxOnChange<T extends MultipleComboboxBaseOption> = (
  values: MultipleComboboxValue,
  options: T[] | undefined,
) => void;

type MultipleComboboxProps<TOptions extends readonly MultipleComboboxBaseOption[]> = {
  options: TOptions;
  value?: MultipleComboboxValue;
  onChange?: MultipleComboboxOnChange<TOptions[number]>;
  autoResize?: boolean;
  limitTags?: number;
  showCode?: boolean;
  label?: string | React.ReactNode;
  error?: string;
  required?: boolean;
  placeholder?: string;
  searchPlaceholder?: string;
  emptyMessage?: string;
  disabled?: boolean;
  readonly?: boolean;
  size?: FormSize;
  className?: ComponentProps<'div'>['className'];
  triggerClassName?: ComponentProps<typeof Button>['className'];
  popoverClassName?: ComponentProps<typeof PopoverContent>['className'];
};

function MultipleCombobox<TOptions extends readonly MultipleComboboxBaseOption[]>({
  options,
  value = [],
  onChange,
  autoResize = false,
  limitTags,
  showCode = false,
  label,
  error,
  required,
  placeholder = 'Select options...',
  searchPlaceholder = 'Search...',
  emptyMessage = 'No results found.',
  disabled = false,
  readonly = false,
  size = 'md',
  className,
  triggerClassName,
  popoverClassName,
}: MultipleComboboxProps<TOptions>) {
  const [open, setOpen] = React.useState(false);
  const selectedValues = React.useMemo(() => value || [], [value]);

  const selectedOptions = React.useMemo(
    () => options.filter((opt) => selectedValues.includes(opt.id)) as TOptions[number][],
    [options, selectedValues],
  );

  const handleToggle = (optionId: string) => {
    if (disabled || readonly) return;

    const isSelected = selectedValues.includes(optionId);
    const newValues = isSelected
      ? selectedValues.filter((id) => id !== optionId)
      : [...selectedValues, optionId];

    const newOptions = options.filter((opt) => newValues.includes(opt.id)) as TOptions[number][];
    onChange?.(newValues, newOptions.length > 0 ? newOptions : undefined);
  };

  const handleRemove = (optionId: string, e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (disabled || readonly) return;

    const newValues = selectedValues.filter((id) => id !== optionId);
    const newOptions = options.filter((opt) => newValues.includes(opt.id)) as TOptions[number][];
    onChange?.(newValues, newOptions.length > 0 ? newOptions : undefined);
  };

  const handleClearAll = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (disabled || readonly) return;
    onChange?.([], undefined);
  };

  const getBadgeContent = (option: TOptions[number]) => {
    if (showCode && option.code !== undefined) {
      return `${option.code} - ${option.name}`;
    }
    return option.name;
  };

  const displayedBadges = limitTags ? selectedOptions.slice(0, limitTags) : selectedOptions;
  const remainingCount = limitTags ? Math.max(0, selectedOptions.length - limitTags) : 0;

  const triggerElement = (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          disabled={disabled}
          size={size}
          className={cn(
            'w-full justify-between group/combobox',
            !selectedValues.length && 'text-muted-foreground',
            error && 'border-destructive',
            autoResize && 'h-auto min-h-0',
            triggerClassName,
          )}
        >
          <div
            className={cn(
              'flex-1 flex items-center gap-1.5 min-w-0',
              autoResize ? 'flex-wrap' : 'overflow-hidden',
            )}
          >
            {selectedValues.length === 0 ? (
              <span className="text-left truncate">{placeholder}</span>
            ) : (
              <>
                {displayedBadges.map((option) => (
                  <Badge
                    key={option.id}
                    variant="secondary"
                    className={cn(
                      'gap-1 shrink-0',
                      !autoResize && 'truncate max-w-[120px]',
                      (disabled || readonly) && 'pr-2',
                    )}
                  >
                    <span className="truncate">{getBadgeContent(option)}</span>
                    {!disabled && !readonly && (
                      <button
                        type="button"
                        onMouseDown={(e) => {
                          e.preventDefault();
                          e.stopPropagation();
                        }}
                        onClick={(e) => handleRemove(option.id, e)}
                        className="ml-0.5 hover:bg-destructive/20 rounded-full p-0.5 -mr-1 shrink-0"
                      >
                        <X className="h-2.5 w-2.5" />
                      </button>
                    )}
                  </Badge>
                ))}
                {remainingCount > 0 && (
                  <Badge variant="secondary" className="shrink-0">
                    +{remainingCount}
                  </Badge>
                )}
              </>
            )}
          </div>
          <div className="flex items-center gap-1 shrink-0 ml-2">
            {selectedValues.length > 0 && !disabled && !readonly ? (
              <span
                onMouseDown={(e) => {
                  e.preventDefault();
                  e.stopPropagation();
                }}
                onClick={handleClearAll}
                className="cursor-pointer"
              >
                <XCircle className="text-muted-foreground hidden group-hover/combobox:block h-4 w-4" />
                <ChevronDown className="text-muted-foreground block group-hover/combobox:hidden h-4 w-4" />
              </span>
            ) : (
              <ChevronDown className="text-muted-foreground h-4 w-4" />
            )}
          </div>
        </Button>
      </PopoverTrigger>
      <PopoverContent
        className={cn('w-(--radix-popover-trigger-width) p-0', popoverClassName)}
        align="start"
      >
        <Command>
          <CommandInput placeholder={searchPlaceholder} />
          <CommandList>
            <CommandEmpty>{emptyMessage}</CommandEmpty>
            <CommandGroup>
              {options.map((option) => {
                const isSelected = selectedValues.includes(option.id);
                return (
                  <CommandItem
                    key={option.id}
                    value={option.name}
                    disabled={option.disabled}
                    onSelect={() => {
                      handleToggle(option.id);
                    }}
                    className="flex items-center gap-2 cursor-pointer"
                  >
                    <div
                      onMouseDown={(e) => e.preventDefault()}
                      onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        handleToggle(option.id);
                      }}
                      className="shrink-0"
                    >
                      <Checkbox
                        checked={isSelected}
                        disabled={option.disabled || disabled || readonly}
                        size={size}
                      />
                    </div>
                    <div className="flex-1 flex items-center gap-2 min-w-0">
                      {showCode && option.code !== undefined && (
                        <span className="text-muted-foreground shrink-0">{option.code}</span>
                      )}
                      <span className="flex-1 truncate">{option.name}</span>
                    </div>
                    <CheckIcon
                      className={cn(
                        'h-4 w-4 shrink-0 text-primary',
                        isSelected ? 'opacity-100' : 'opacity-0',
                      )}
                    />
                  </CommandItem>
                );
              })}
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );

  if (!label && !error) {
    return <div className={cn('w-full', className)}>{triggerElement}</div>;
  }

  return (
    <div className={cn('w-full', className)}>
      {label && (
        <Label className="text-foreground leading-5 block">
          {label}
          {required && <span className="text-destructive">*</span>}
        </Label>
      )}
      <div className={label ? 'mt-1' : ''}>{triggerElement}</div>
      {error && (
        <div className="text-destructive text-sm mt-1.5" role="alert">
          {error}
        </div>
      )}
    </div>
  );
}

export {
  MultipleCombobox,
  type MultipleComboboxBaseOption,
  type MultipleComboboxOnChange,
  type MultipleComboboxProps,
  type MultipleComboboxValue
};

