        sizeConfig.height,
        sizeConfig.text,
        sizeConfig.padding,
        {
          "[&_svg:not([class*='size-'])]:size-3": size === 'xxs',
          "[&_svg:not([class*='size-'])]:size-3.5": size === 'xs',
          "[&_svg:not([class*='size-'])]:size-4": size === 'sm' || size === 'md',
          "[&_svg:not([class*='size-'])]:size-5": size === 'lg' || size === 'xl',
          "[&_svg:not([class*='size-'])]:size-6": size === 'xxl',
