import * as React from 'react';

type NavigationDirection = 'horizontal' | 'vertical' | 'both';

interface UseKeyboardNavigationOptions {
  direction?: NavigationDirection;
  loop?: boolean;
  onNavigate?: (index: number) => void;
}

export function useKeyboardNavigation(
  itemCount: number,
  options: UseKeyboardNavigationOptions = {},
): {
  currentIndex: number;
  setCurrentIndex: React.Dispatch<React.SetStateAction<number>>;
  handleKeyDown: (e: React.KeyboardEvent) => void;
} {
  const { direction = 'both', loop = false, onNavigate } = options;
  const [currentIndex, setCurrentIndex] = React.useState(0);

  const handleKeyDown = React.useCallback(
    (e: React.KeyboardEvent) => {
      let newIndex = currentIndex;

      if (direction === 'horizontal' || direction === 'both') {
        if (e.key === 'ArrowLeft') {
          e.preventDefault();
          newIndex = currentIndex > 0 ? currentIndex - 1 : loop ? itemCount - 1 : currentIndex;
        } else if (e.key === 'ArrowRight') {
          e.preventDefault();
          newIndex = currentIndex < itemCount - 1 ? currentIndex + 1 : loop ? 0 : currentIndex;
        }
      }

      if (direction === 'vertical' || direction === 'both') {
        if (e.key === 'ArrowUp') {
          e.preventDefault();
          newIndex = currentIndex > 0 ? currentIndex - 1 : loop ? itemCount - 1 : currentIndex;
        } else if (e.key === 'ArrowDown') {
          e.preventDefault();
          newIndex = currentIndex < itemCount - 1 ? currentIndex + 1 : loop ? 0 : currentIndex;
        }
      }

      if (newIndex !== currentIndex) {
        setCurrentIndex(newIndex);
        onNavigate?.(newIndex);
      }
    },
    [currentIndex, itemCount, direction, loop, onNavigate],
  );

  return { currentIndex, setCurrentIndex, handleKeyDown };
}

import * as React from 'react';

type SetValue<T> = React.Dispatch<React.SetStateAction<T>>;

export function useLocalStorage<T>(key: string, initialValue: T): [T, SetValue<T>, () => void] {
  const [storedValue, setStoredValue] = React.useState<T>(() => {
    if (typeof window === 'undefined') {
      return initialValue;
    }

    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error(`Error reading localStorage key "${key}":`, error);
      return initialValue;
    }
  });

  const setValue: SetValue<T> = React.useCallback(
    (value) => {
      try {
        const valueToStore = value instanceof Function ? value(storedValue) : value;
        setStoredValue(valueToStore);

        if (typeof window !== 'undefined') {
          window.localStorage.setItem(key, JSON.stringify(valueToStore));
        }
      } catch (error) {
        console.error(`Error setting localStorage key "${key}":`, error);
      }
    },
    [key, storedValue],
  );

  const removeValue = React.useCallback(() => {
    try {
      setStoredValue(initialValue);
      if (typeof window !== 'undefined') {
        window.localStorage.removeItem(key);
      }
    } catch (error) {
      console.error(`Error removing localStorage key "${key}":`, error);
    }
  }, [key, initialValue]);

  return [storedValue, setValue, removeValue];
}

import * as React from 'react';

export function useMediaQuery(query: string): boolean {
  const [matches, setMatches] = React.useState<boolean>(() => {
    if (typeof window === 'undefined') return false;
    return window.matchMedia(query).matches;
  });

  React.useEffect(() => {
    if (typeof window === 'undefined') return;

    const mediaQuery = window.matchMedia(query);
    const handler = (event: MediaQueryListEvent) => {
      setMatches(event.matches);
    };

    // Modern browsers
    if (mediaQuery.addEventListener) {
      mediaQuery.addEventListener('change', handler);
      return () => mediaQuery.removeEventListener('change', handler);
    } else {
      // Fallback for older browsers
      mediaQuery.addListener(handler);
      return () => mediaQuery.removeListener(handler);
    }
  }, [query]);

  return matches;
}

import type { DependencyList } from 'react';
import * as React from 'react';

export function useMemoizedCallback<T extends (...args: unknown[]) => unknown>(
  callback: T,
  deps: DependencyList,
): T {
  // eslint-disable-next-line react-hooks/exhaustive-deps
  return React.useCallback(callback, deps) as T;
}

import * as React from 'react';

const MOBILE_BREAKPOINT = 768;

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined);

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`);
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    };
    mql.addEventListener('change', onChange);
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
    return () => mql.removeEventListener('change', onChange);
  }, []);

  return !!isMobile;
}

import * as React from 'react';

export function usePrevious<T>(value: T): T | undefined {
  const [previous, setPrevious] = React.useState<T | undefined>(undefined);
  const currentRef = React.useRef<T>(value);

  React.useEffect(() => {
    if (currentRef.current !== value) {
      setPrevious(currentRef.current);
      currentRef.current = value;
    }
  }, [value]);

  return previous;
}

