'use client';

import { FileUpload, type FileUploadProps } from '@/components/ui/file-upload';
import { Label } from '@/components/ui/label';
import { cn, get } from '@/lib/utils';
import type { ComponentProps } from 'react';
import type { Control, FieldValues, Path } from 'react-hook-form';
import { Controller, useFormState } from 'react-hook-form';

export type RHFFileUploadProps<T extends FieldValues = FieldValues> = Omit<
  FileUploadProps,
  'value' | 'onChange' | 'onError'
> & {
  control: Control<T>;
  name: Path<T>;
  label?: string;
  error?: string;
  required?: boolean;
  wrapperClassName?: ComponentProps<'div'>['className'];
};

export function RHFFileUpload<T extends FieldValues = FieldValues>({
  control,
  name,
  label,
  error,
  required = false,
  wrapperClassName,
  ...fileUploadProps
}: RHFFileUploadProps<T>) {
  const formState = useFormState({ control, name });
  const fieldError = error || get<string>(formState.errors, `${name}.message`);

  return (
    <div className={cn('w-full', wrapperClassName)} data-slot="rhf-file-upload">
      {label && (
        <Label htmlFor={name} className="mb-2 block">
          {label}
          {required && <span className="text-destructive ml-1">*</span>}
        </Label>
      )}
      <Controller
        control={control}
        name={name}
        render={({ field: { onChange, value, ...field } }) => {
          const files = value ? (Array.isArray(value) ? value : [value]).filter(Boolean) : [];

          return (
            <FileUpload
              {...fileUploadProps}
              {...field}
              value={files}
              onChange={(newFiles) => {
                if (fileUploadProps.multiple) {
                  onChange(newFiles);
                } else {
                  onChange(newFiles.length > 0 ? newFiles[0] : undefined);
                }
              }}
              onError={(errorMessage) => {
                // You can handle error here if needed
                console.error('File upload error:', errorMessage);
              }}
              disabled={fileUploadProps.disabled}
            />
          );
        }}
      />
      {fieldError && (
        <p className="text-sm text-destructive mt-1" data-slot="rhf-file-upload-error">
          {fieldError}
        </p>
      )}
    </div>
  );
}

'use client';

import { Input } from '@/components/ui/input';
import { cn } from '@/lib/utils';
import type { ComponentProps } from 'react';
import { Controller, useFormState } from 'react-hook-form';
import type { Control, FieldValues, Path } from 'react-hook-form';
import { get } from '@/lib/utils';

export type FormatType = 'text' | 'integer' | 'decimal' | 'currency';

export type RHFFormattedInputProps<T extends FieldValues = FieldValues> = Omit<
  ComponentProps<typeof Input>,
  'value' | 'onChange' | 'type' | 'label' | 'error' | 'required'
> & {
  control: Control<T>;
  name: Path<T>;
  label?: string;
  format?: FormatType;
  required?: boolean;
  wrapperClassName?: ComponentProps<'div'>['className'];
  callback?: (value: string) => void;
};

const formatValue = (value: string, format: FormatType): string => {
  if (format === 'text' || !value) return value;

  if (format === 'integer' || format === 'currency') {
    return value.replace(/\D/g, '');
  }

  if (format === 'decimal') {
    return value.replace(/[^\d.,]/g, '').replace(/\./g, ',');
  }

  return value;
};

export function RHFFormattedInput<T extends FieldValues = FieldValues>({
  control,
  name,
  label,
  format = 'text',
  required,
  wrapperClassName,
  callback = () => {},
  ...inputProps
}: RHFFormattedInputProps<T>) {
  const formState = useFormState({ control, name });
  const error = get<string>(formState.errors, `${name}.message`);

  return (
    <Controller
      control={control}
      name={name}
      render={({ field }) => {
        return (
          <div className={cn('w-full', wrapperClassName)}>
            <Input
              {...inputProps}
              {...field}
              type="text"
              label={label}
              error={error}
              required={required}
              value={field.value || ''}
              onChange={(e) => {
                const formatted = formatValue(e.target.value, format);
                field.onChange(formatted);
                callback(formatted);
              }}
            />
          </div>
        );
      }}
    />
  );
}

